import { useEffect } from "react";

import classes from "./AvailableGroceries.module.css";
import useHttp from "../../hooks/use-http";
import MealList from "./GroceryList";

const AvailableMeals = () => {
  const {
    isLoading: mealsIsLoading,
    sendRequest: sendGroceriesRequest,
    response: mealsResponse,
  } = useHttp();

  //Request to backend for fetching groceries
  const FetchGroceries = async (requestJson) => {
    sendGroceriesRequest({
      url: "http://localhost:8080/api/protected/fetchGroceries",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {},
      errorMsg: "Request failed!",
    });
  };

  useEffect(() => {
    const test = "Hi";
    const requestJson = {test};
    FetchGroceries(requestJson);
  }, []);

  console.log(mealsIsLoading);

  if (mealsIsLoading) {
    return (
      <section className={classes.MealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  return <MealList meals={mealsResponse} />;
};

export default AvailableMeals;
