import MealItem from "./GroceryItem/GroceryItem";
import Card from "../UI/Card";
import classes from "./AvailableGroceries.module.css";

function MealList(props) {
  const mealsList = props.meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <section className={classes.categories}>
        <h1>Categories</h1>
        <div className={classControlClasses}>
          <label>Class</label>
          <select
            ref={classInputRef}
            className={classes.select}
            name="classList"
            id="class"
          >
            <option value="" style={{ color: "grey" }}>
              Select an Option
            </option>
            <option value="Economy" style={{ color: "black" }}>
              Economy
            </option>
            <option value="Premium" style={{ color: "black" }}>
              Premium
            </option>
            <option value="Executive" style={{ color: "black" }}>
              Executive
            </option>
          </select>
        </div>
      </section>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
}
export default MealList;
