import { useEffect, useState } from 'react';

import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import classes from './AvailableMeals.module.css';
import useHttp from "../../hooks/use-http";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();

  const {
    isLoading: mealsIsLoading,
    error: mealsError,
    sendRequest: sendMealsRequest,
    response: mealsResponse,
    resetError: resetMealsError,
    resetResponse: resetMealsResponse,
  } = useHttp();

  //Request to backend for verification email
  const FetchMeals = async (requestJson) => {
    sendMealsRequest({
      url: "https://space-ticket-booking-java.herokuapp.com/api/protected/verifyUser",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {},
      errorMsg: "Request failed!",
    });
  };

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        'https://react-http-6b4a6.firebaseio.com/meals.json'
      );

      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();

      const loadedMeals = [];

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }

      setMeals(loadedMeals);
      setIsLoading(false);
    };

    fetchMeals().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={classes.MealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  if (httpError) {
    return (
      <section className={classes.MealsError}>
        <p>{httpError}</p>
      </section>
    );
  }

  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
